SELECT "Generico_5"."Vehiculo" AS "Vehiculo",   SUM("Generico_5"."InversionUS") AS "sum:InversionUS:ok" FROM "Generico_5" WHERE (("Generico_5"."Anunciante" IN ('BANTRAB/TODOTICKET', 'TODOTICKET', 'TODOTICKET.COM')) AND (CAST(EXTRACT(YEAR FROM "Generico_5"."FECHA") AS BIGINT) >= 2010) AND (CAST(EXTRACT(YEAR FROM "Generico_5"."FECHA") AS BIGINT) <= 2015) AND (CAST(EXTRACT(YEAR FROM "Generico_5"."FECHA") AS BIGINT) IN (2014, 2015)) AND ("Generico_5"."Medio" = 'RADIO') AND (CAST(EXTRACT(MONTH FROM "Generico_5"."FECHA") AS BIGINT) IN (1, 2, 3, 4, 5))) GROUP BY "Generico_5"."Vehiculo" ORDER BY "Vehiculo";
