SELECT "Generico_5"."Anunciante" AS "Datos (copia)",   CAST(EXTRACT(MONTH FROM "Generico_5"."FECHA") AS BIGINT) AS "mn:FECHA:ok",   SUM("Generico_5"."InversionUS") AS "sum:Calculation_0061002123102817:ok",   CAST(EXTRACT(YEAR FROM "Generico_5"."FECHA") AS BIGINT) AS "yr:FECHA:ok" FROM "Generico_5" WHERE (("Generico_5"."Anunciante" IN ('BANTRAB/TODOTICKET', 'TODOTICKET', 'TODOTICKET.COM')) AND (CAST(EXTRACT(YEAR FROM "Generico_5"."FECHA") AS BIGINT) IN (2014, 2015))) GROUP BY "Generico_5"."Anunciante", "mn:FECHA:ok", "yr:FECHA:ok" ORDER BY "Datos (copia)";
