SELECT "PanCreactomy2_2"."NPPES_PROVIDER_LAST_ORG_NAME" AS "NPPES_PROVIDER_LAST_ORG_NAME",   "PanCreactomy2_2"."NPPES_PROVIDER_STATE" AS "NPPES_PROVIDER_STATE",   CAST("PanCreactomy2_2"."NPPES_PROVIDER_ZIP" AS BIGINT) AS "NPPES_PROVIDER_ZIP",   "PanCreactomy2_2"."PROVIDER_TYPE" AS "PROVIDER_TYPE",   SUM("PanCreactomy2_2"."LINE_SRVC_CNT") AS "sum:LINE_SRVC_CNT:ok" FROM "PanCreactomy2_2" WHERE ((("PanCreactomy2_2"."PROVIDER_TYPE" NOT IN ('', 'Clinical Laboratory')) OR ("PanCreactomy2_2"."PROVIDER_TYPE" IS NULL)) AND ("PanCreactomy2_2"."HCPCS_DESCRIPTION" IN ('Insertion of pancreatic or bile duct drainage tube using an endoscope', 'Insertion of tube or stent into bile or pancreatic duct using an endoscope', 'Placement of catheter of gallbladder and pancreas under imaging using an endoscope'))) GROUP BY "PanCreactomy2_2"."NPPES_PROVIDER_LAST_ORG_NAME",   "PanCreactomy2_2"."NPPES_PROVIDER_STATE",   "PanCreactomy2_2"."NPPES_PROVIDER_ZIP",   "PanCreactomy2_2"."PROVIDER_TYPE",   "PanCreactomy2_2"."NPPES_PROVIDER_ZIP" ORDER BY "NPPES_PROVIDER_LAST_ORG_NAME";
