SELECT "RealEstate2_3"."Postcode_District" AS "Postcode_District" FROM "RealEstate2_3" WHERE ((locate('THE BISHOPS AVENUE',"RealEstate2_3"."Street")>0) AND ("RealEstate2_3"."County" = 'GREATER LONDON') AND ("RealEstate2_3"."Date_of_Transfer" >= cast('1996-01-01' as DATE)) AND ("RealEstate2_3"."Date_of_Transfer" < cast('2019-01-01' as DATE)) AND ("RealEstate2_3"."Property_Type" <> 'O')) GROUP BY "RealEstate2_3"."Postcode_District";
