SELECT "TableroSistemaPenal_5"."PAÍS" AS "PAÍS",   COUNT(DISTINCT "TableroSistemaPenal_5"."ID_EVENTO") AS "ctd:ID_EVENTO:ok",   CAST(EXTRACT(YEAR FROM "TableroSistemaPenal_5"."FECHA AUDIENCIA") AS BIGINT) AS "yr:FECHA AUDIENCIA:ok" FROM "TableroSistemaPenal_5" WHERE ("TableroSistemaPenal_5"."ILEGALIDAD_DETENCION" = cast('1' as double)) GROUP BY "TableroSistemaPenal_5"."PAÍS",   "yr:FECHA AUDIENCIA:ok" ORDER BY "PAÍS";
