SELECT "TableroSistemaPenal_6"."AUDIENCIA" AS "AUDIENCIA",   SUM(CAST("TableroSistemaPenal_6"."Number of Records" AS BIGINT)) AS "sum:Number of Records:ok",   CAST(EXTRACT(YEAR FROM "TableroSistemaPenal_6"."FECHA AUDIENCIA") AS BIGINT) AS "yr:FECHA AUDIENCIA:ok" FROM "TableroSistemaPenal_6" WHERE (("TableroSistemaPenal_6"."AUDIENCIA" IN ('AUDIENCIA DE JUICIO ORAL TOP', 'AUDIENCIA DE LECTURA DE SENTENCIA.')) AND ("TableroSistemaPenal_6"."TIP TRIB" = 'Oral')) GROUP BY "TableroSistemaPenal_6"."AUDIENCIA",   "yr:FECHA AUDIENCIA:ok" ORDER BY "AUDIENCIA";
