SELECT "TableroSistemaPenal_8"."Number of Records" AS "Number of Records",   "TableroSistemaPenal_8"."PAÍS" AS "PAÍS",   "TableroSistemaPenal_8"."Tipo Sentencia" AS "Tipo Sentencia",   SUM(CAST("TableroSistemaPenal_8"."Number of Records" AS BIGINT)) AS "sum:Number of Records:ok" FROM "TableroSistemaPenal_8" WHERE ("TableroSistemaPenal_8"."Tipo Sentencia" IN ('Sentencia Absolutoria', 'Sentencia Condenatoria')) GROUP BY "TableroSistemaPenal_8"."Number of Records",   "TableroSistemaPenal_8"."PAÍS", "TableroSistemaPenal_8"."Tipo Sentencia" ORDER BY "Number of Records";
