    rel4,
    list(
      {
        tmp_expr <- expr_reference("a")
        expr_set_alias(tmp_expr, "a_y")
        tmp_expr
      },
      {
        tmp_expr <- expr_reference("b")
        expr_set_alias(tmp_expr, "b_y")
        tmp_expr
      }
    )
  )
