    rel5,
    list(
      {
        tmp_expr <- expr_reference("a_x")
        expr_set_alias(tmp_expr, "a_x")
        tmp_expr
      },
      {
        tmp_expr <- expr_reference("b_x")
        expr_set_alias(tmp_expr, "b_x")
        tmp_expr
      },
      {
        tmp_expr <- expr_window(expr_function("row_number", list()), list(), list(), offset_expr = NULL, default_expr = NULL)
        expr_set_alias(tmp_expr, "___row_number_x")
        tmp_expr
      }
    )
  )
  rel8 <- rel_project(
    rel6,
    list(
      {
        tmp_expr <- expr_reference("a_y")
        expr_set_alias(tmp_expr, "a_y")
        tmp_expr
      },
      {
        tmp_expr <- expr_reference("b_y")
        expr_set_alias(tmp_expr, "b_y")
        tmp_expr
      },
